mapper.select:
/* UserMybatisRepository.countListFriendToAddGroup */ select count(1) from channel c1 join user_channel uc1 on (c1.id = uc1.channel_id and uc1.user_id = #{userId} and uc1.status = 'ACCEPT' and c1.type = 'FRIEND') join user_channel uc2 on (c1.id = uc2.channel_id and uc2.user_id != #{userId} and uc2.status = 'ACCEPT' and c1.type = 'FRIEND') join user u1 on (uc2.user_id = u1.id
and (u1.name like #{pattern} or u1.email like #{pattern})
)

/* UserMybatisRepository.getListFriendToAddGroup */ select u1.id id, u1.email email, u1.name name, u1.avatar_url avatarUrl,
uc3.status status from channel c1 join user_channel uc1 on (c1.id = uc1.channel_id and uc1.user_id = #{userId} and uc1.status = 'ACCEPT' and c1.type = 'FRIEND') join user_channel uc2 on (c1.id = uc2.channel_id and uc2.user_id != #{userId} and uc2.status = 'ACCEPT' and c1.type = 'FRIEND') join user u1 on (uc2.user_id = u1.id
and (u1.name like #{pattern} or u1.email like #{pattern})
) left join user_channel uc3 on (u1.id = uc3.user_id and uc3.channel_id = #{channelId} and uc3.channel_id != c1.id) order by u1.name asc limit #{offset}, #{limit}
